import React, { useState } from "react";
import "../index.css";
import image from "../img/farm.jpeg";

let pushcarts = [];

function Market ({ data, onSetCart }) {

  const [cart, setCart] = useState([]);

  let items = data;

  const handleAddToCart = (item) => {
    // Check if the item is already in the cart
    const existingCartItem = cart.find((cartItem) => cartItem.productId === item.productId);

    if (existingCartItem) {
      // If the item is in the cart, update its quantity
      setCart((prevCart) =>
        prevCart.map((cartItem) =>
          cartItem.productId === item.productId
            ? { ...cartItem, cartQuantity: cartItem.cartQuantity + 1 }
            : cartItem
        )
      );
    } else {
      // If the item is not in the cart, add it with quantity 1
      setCart((prevCart) => [...prevCart, { ...item, cartQuantity: 1 }]);
    }
  };

  return (
    <>
      <div className="homepage-item">
        <div className="homepage-item-child">
          <h3>Marketplace</h3>
          <ul>
            {
              items.map((item) => (
              <li key={item.productId}>
                <img src={image} alt="Appliances" />
                <p className="title">{item.productName}</p>
                <p className="desc">Quantity: {item.productQuantity}</p>
                <p className="desc">
                {item.productDesc.length > 100 ? `${item.productDesc.slice(0, 100)}...` : item.productDesc}
                </p>

                <input type="number" name="cartQuantity" className="bootstrap" min="0" max={item.productQuantity} value={item.cartQuantity} placeholder="Product QTY" />

                <p className="desc">Type: {item.productType === 1 ? "üåæ Crop" : item.productType === 2 ? "üêî Poultry" : "No classification"}</p>
                <button className="amber-button" onClick={() => handleAddToCart(item)}>Add to Cart</button>
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="homepage-item">
        <ul>
          { pushcarts.map((pushcart) => (
            pushcart
          ))}
        </ul>
      </div>
    </>
  )
}

export default Market;
