import React, { useState } from "react";
import "../index.css";
import image from "../img/farm.jpeg";

let pushcarts = [];

function Market ({ data, onSetCart }) {

  let items = data;
  const [selectedItems, setSelectedItems] = useState(data);


  const handleAddToCart = (item) => {
    onSetCart(item);
  };

  const handleChanges = (e, item) => {
    const newQuantity = parseInt(e.target.value, 10) || 0;

    // Update the items array with the new quantity for the specific item
    const updatedItems = items.map((product) =>
      product.productId === item.productId
        ? { ...product, cartQuantity: newQuantity }
        : product
    );

    setSelectedItems(updatedItems);
  };

  return (
    <>
      <div className="homepage-item">
        <div className="homepage-item-child">
          <h3>Marketplace</h3>
          <ul>
            {
              items.map((item) => (
              <li key={item.productId}>
                <img src={image} alt="Appliances" />
                <p className="title">{item.productName}</p>
                <p className="desc">Quantity: {item.productQuantity}</p>
                <p className="desc">
                {item.productDesc.length > 100 ? `${item.productDesc.slice(0, 100)}...` : item.productDesc}
                </p>

                <input type="number" name="cartQuantity" className="bootstrap" min="0" max={item.productQuantity} value={item.cartQuantity} onChange={(e) => handleChanges(e, item)} placeholder="Product QTY" />
                <p className="desc">Type: {item.productType === 1 ? "üåæ Crop" : item.productType === 2 ? "üêî Poultry" : "No classification"}</p>
                <button className="amber-button" onClick={() => handleAddToCart(item)}>Add to Cart</button>
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="homepage-item">
        <ul>
          { pushcarts.map((pushcart) => (
            pushcart
          ))}
        </ul>
      </div>
    </>
  )
}

export default Market;
